function LogicaDoRobo() {
    x = document.getElementById("PontoInicialX");
    y = document.getElementById("PontoInicialY");

    RoboX[0] = parseFloat(x.value);
    RoboY[0] = parseFloat(y.value);
    Distancia[0] = Math.sqrt(Math.pow((BolaX[0] - RoboX[0]), 2) + Math.pow((BolaY[0] - RoboY[0]), 2));




    for (contador=0; contador<1002; contador++){
        aceleracaoBolaX[contador] = -0.01;
        aceleracaoBolaY[contador] = -0.016;
        aceleracao[contador] = 2.6;
    }

    for (contador = 0; contador<1002; contador++){
        velocidadeBolaX[contador] = (-0.01*Tempo[contador]) + 0.5;
    }

    for (contador = 0; contador<1002; contador++){
        velocidadeBolaY[contador] = (-0.016*Tempo[contador]) + 0.4;
    }

    for (contador = 0; contador < 1002; contador++) {

        velocidadeRobo[contador] = aceleracao[contador] * Tempo[contador];

        if (velocidadeRobo[contador] >= velocidadeMaxima) {
            velocidadeRobo[contador] = velocidadeMaxima;
            aceleracao[contador] = 0;
        }
    }

    if (velocidadeRobo >= velocidadeMaxima){
        aceleracao = 0;
    }

    for (contador = 0; contador < 1002; contador++) {

        Distancia[contador] = parseFloat(Math.sqrt((Math.pow((BolaX[contador] - RoboX[contador]), 2) + Math.pow((BolaY[contador] - RoboY[contador]), 2))));

        cos[contador] = parseFloat((BolaX[contador] - RoboX[contador]) / Distancia[contador]); //acha o cosseno
        sen[contador] = parseFloat((BolaY[contador] - RoboY[contador]) / Distancia[contador]); //acha o seno



        RoboX[contador + 1] = parseFloat(RoboX[contador] + (cos[contador] * 0.04)); //vai aumentando a posicao do robo em X
        RoboY[contador+ 1] = parseFloat(RoboY[contador] + (sen[contador] * 0.05)); //vai aumentando a posicao do robo em Y


        if (RoboX[contador] >= BolaX[contador] - raio && RoboX[contador] <= BolaX[contador] + raio && RoboY[contador] >= BolaY[contador] - raio && RoboY[contador] <= BolaY[contador] + raio) { //hitbox - range de 0.125
            tempofinal = Tempo[contador];

            var contadoraux=0;
            for (contadoraux=0;contadoraux<1002;contadoraux++){
                vettempofinal[contadoraux] = Tempo[contadoraux];
                if(vettempofinal[contadoraux] == tempofinal){
                    break;
                }
            }
            aux = tempofinal;
            distaux = Distancia[0] ;
            distaux2 = Distancia[contador];
            alert("Ponto Salvo Com Sucesso!");
            break;
        }

    }

}
