/* ------------------------ Parte do Canvas -----------------------------*/


function canvas(){
    alert("Precione a Tecla 'W' e Segure Para Começar ");
    xaux = parseFloat(x.value);
    yaux = parseFloat(y.value);

    var canvas = document.getElementById("myCanvas");
    var ctx = canvas.getContext("2d");

    var tecla=0;

    document.addEventListener("keydown", function (evento) {
        tecla=evento.keyCode;
    });

    var robozinX= xaux*100;
    var robozinY= 600-(yaux*100);

    var PontoFinalX = RoboX[contador]*100;
    var PontoFinalY =  600-(RoboY[contador]*100);

    var bolinhaX=100;
    var bolinhaY=550;


    var i=0;

    function movimento() {
        if (tecla === 87) {
            robozinX = RoboX[i];
            robozinX = robozinX * 100;
            robozinY = RoboY[i];
            robozinY = 600 - (robozinY * 100);
            bolinhaX = BolaX[i];
            bolinhaX = bolinhaX * 100;
            bolinhaY = BolaY[i];
            bolinhaY = 600 - (bolinhaY * 100);
            i++;
            if (robozinX == PontoFinalX && robozinY == PontoFinalY) {
                i=1;
            }
        }
    }


    function Bolinha(x,y) {
        ctx.beginPath();
        ctx.fillStyle = "orange";
        ctx.arc(x, y, 5, 0, 2*Math.PI);
        ctx.fill();
        ctx.strokeStyle = 'black';
        ctx.lineWidth = 1;
        ctx.stroke();
        ctx.closePath();
    }


    function desenha() {
        ctx.beginPath();
        ctx.fillStyle="green";
        ctx.fillRect(0,0,canvas.width, canvas.height);

        var campo=new Image();
        campo.src='campo.png';
        ctx.drawImage(campo, 0,0, canvas.width, canvas.height);

        var robozin = new Image();
        robozin.src='robozin.png';
        ctx.drawImage(robozin, robozinX,robozinY, 36, 30);

        ctx.closePath();

    }

    function atualiza(){
        requestAnimationFrame(atualiza);
        desenha();
        Bolinha(bolinhaX,bolinhaY);
        movimento();
    }
    atualiza();
}
